import React, { useState } from 'react';
import './chat.css';

function Chat() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([
    { text: "Hello! I'm the Bison Advisor. How can I assist you today?", sender: 'advisor' },
  ]);
  const [showIntro, setShowIntro] = useState(true);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSendMessage = async () => {
    setMessages((prevMessages) => [...prevMessages, { text: input, sender: 'user' }]);
    setInput('');
    setShowIntro(false);

    // Request AI response
    await handleAIResponse();
  };

  const handleAIResponse = async () => {
    try {
      const aiResponseMessage = ":AI-Driven Recommendations: The system generates personalized advising recommendations for students.";

      const conversation = [
        { role: 'user', content: input },
        { role: 'advisor', content: aiResponseMessage },
      ];

      console.log('Conversation:', conversation);

      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer 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',
        },
        body: JSON.stringify({
          model: 'gpt-3.5-turbo',
          messages: conversation,
        }),
      });

      console.log('OpenAI Response:', response);

      if (!response.ok) {
        throw new Error(`Failed to fetch AI response. Status: ${response.status}`);
      }

      const data = await response.json();
      const aiResponse = data.choices[0].message.content;

      console.log('AI Response:', aiResponse);

      setMessages((prevMessages) => [
        ...prevMessages,
        { text: aiResponse, sender: 'advisor' },
      ]);

    } catch (error) {
      console.error('Error fetching AI response:', error.message);
    }
  };

  return (
    <div className="ChatContainer">
      {showIntro && (
        <div className="IntroSection">
          <img src="https://upload.wikimedia.org/wikipedia/en/b/b4/Howard_Bison_logo.svg" alt="Logo" />
          <p>How can I help you today?</p>
        </div>
      )}
      <div className="ChatBox">
        <div className="Messages">
          {messages.map((message, index) => (
            <div key={index} className={`Message ${message.sender}`}>
              {message.text}
            </div>
          ))}
        </div>
        <div className="InputBox">
          <input
            type="text"
            placeholder="Type a message..."
            value={input}
            onChange={handleInputChange}
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
