import React, { useState } from 'react';
import './chat.css';

function Chat() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [chatStarted, setChatStarted] = useState(false);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSendMessage = async () => {
    setMessages((prevMessages) => [...prevMessages, { text: input, sender: 'user' }]);
    setInput('');
    setChatStarted(true);

    // Send the user's message to the server and handle AI response
    await handleAIResponse(input);
  };

  const handleAIResponse = async (userMessage) => {
    try {
      // Prepare the conversation format for OpenAI
      const conversation = [
        { role: 'user', content: userMessage },
        ...messages.map((msg) => ({ role: msg.sender, content: msg.text })),
      ];

      // Log the conversation for debugging
      console.log('Conversation:', conversation);

      // Make a POST request to OpenAI API
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer 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', // Replace with your OpenAI API key
        },
        body: JSON.stringify({
          model: 'gpt-3.5-turbo',
          messages: conversation,
        }),
      });

      // Log the OpenAI response for debugging
      console.log('OpenAI Response:', response);

      if (!response.ok) {
        throw new Error('Failed to fetch AI response');
      }

      const data = await response.json();

      // Extract the assistant's reply from the OpenAI response
      const aiResponse = data.choices[0].message.content;

      setMessages((prevMessages) => [...prevMessages, { text: aiResponse, sender: 'advisor' }]);
    } catch (error) {
      console.error('Error fetching AI response:', error.message);
    }
  };

  return (
    <div className="ChatBox">
      <div className={`InitialMessage ${chatStarted ? 'Hidden' : ''}`}>
        <p>How can I help you today?</p>
        {/* Add your logo here */}
      </div>
      <div className={`Messages ${chatStarted ? '' : 'Hidden'}`}>
        {messages.map((message, index) => (
          <div key={index} className={`Message ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      <div className={`InputBox ${chatStarted ? '' : 'Hidden'}`}>
        <input
          type="text"
          placeholder="Type a message..."
          value={input}
          onChange={handleInputChange}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
}

export default Chat;
